mapper.select:
SELECT 

FROM bidcomp_manage a

WHERE a.id = #{id}

SELECT 

FROM bidcomp_manage a

WHERE a.biddate = #{bidDate}

SELECT 

FROM bidcomp_manage a

WHERE a.companyid = #{companyId}

SELECT 

FROM bidcomp_manage a

WHERE a.companyid = #{company.id}

SELECT
FROM bidcomp_manage a
${dataScope}
AND p.programname LIKE '%'||#{program.programName}||'%' '%'+#{program.programName}+'%' concat('%',#{program.programName},'%')
AND c.companyname LIKE '%'||#{company.companyName}||'%' '%'+#{company.companyName}+'%' concat('%',#{company.companyName},'%')
AND a.bidDate BETWEEN #{beginBidDate} AND #{endBidDate}
AND a.isbid = #{isBid}
ORDER BY ${page.orderBy}
ORDER BY p.programname DESC, a.biddate ASC

SELECT 

FROM bidcomp_manage a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bidcomp_manage SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
