mapper.select:
SELECT 

FROM worker a

WHERE a.id = #{id}

SELECT
FROM worker a
${dataScope}
AND a.workernum LIKE '%'||#{workerNum}||'%' '%'+#{workerNum}+'%' concat('%',#{workerNum},'%')
AND a.workername LIKE '%'||#{workerName}||'%' '%'+#{workerName}+'%' concat('%',#{workerName},'%')
AND a.workerposition = #{workerPosition}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM worker a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE worker SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
