mapper.select:
SELECT 
		
FROM company_tab a

WHERE a.id = #{id}

SELECT 
	
FROM company_tab a


	
	${dataScope}
	
		AND a.companynum = #{companyNum}
	
	
		AND a.companyname = #{companyName}
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.companynum ASC

SELECT 
	
FROM company_tab a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.companynum ASC

mapper.update:
UPDATE company_tab SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
