mapper.select:
SELECT 
	
FROM program_contract a
	
WHERE a.id = #{id}

SELECT 
	
FROM program_contract a
	

	
		AND a.program_id = #{program.id}

SELECT 
	
FROM program_contract a
	
WHERE contract_status = #{contractStatus}

SELECT 
	
FROM program_contract a
	
WHERE p.get_method = #{program.getMethod}

SELECT 
	
FROM program_contract a
	
WHERE p.get_method = #{program.getMethod}

SELECT
FROM program_contract a
${dataScope}
AND a.contract_name LIKE '%'||#{contractName}||'%' '%'+#{contractName}+'%' concat('%',#{contractName},'%')
AND p.programname LIKE '%'||#{program.programName}||'%' '%'+#{program.programName}+'%' concat('%',#{program.programName},'%')
AND u.name LIKE '%'||#{user.name}||'%' '%'+#{user.name}+'%' concat('%',#{user.name},'%')
AND a.contract_date BETWEEN #{beginContractDate} AND #{endContractDate}
AND a.contract_status = #{contractStatus}
AND a.approval_status = #{approvalStatus}
AND o.name LIKE '%'||#{program.office.name}||'%' '%'+#{program.office.name}+'%' concat('%',#{user.name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 
	
FROM program_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE program_contract SET 
	approval_status = #{approvalStatus},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE program_contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
