mapper.select:
SELECT
FROM subpro_contract_approval a
${dataScope}
AND spc.subpro_contract_name LIKE '%'||#{subProContract.subProContractName}||'%' '%'+#{subProContract.subProContractName}+'%' concat('%',#{subProContract.subProContractName},'%')
AND sp.subpackage_programname LIKE '%'||#{subProContract.subpackageProgram.subpackageProgramName}||'%' '%'+#{subProContract.subpackageProgram.subpackageProgramName}+'%' concat('%',#{subProContract.subpackageProgram.subpackageProgramName},'%')
AND c.companyname LIKE '%'||#{subProContract.subBidCompany.company.companyName}||'%' '%'+#{subProContract.subBidCompany.company.companyName}+'%' concat('%',#{subProContract.subBidCompany.company.companyName},'%')
AND spc.subpro_contract_date BETWEEN #{subProContract.beginContractDate} AND #{subProContract.endContractDate}
AND a.status = #{subContractStatus}
AND a.approval_status = #{approvalStatus}
AND cb.isBid=1
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 
	
FROM subpro_contract_approval a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
	AND cb.isBid=1
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE subpro_contract_approval SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
