mapper.select:
SELECT 
	
FROM sub_pro_contract a
	

	
		AND sp.id = #{subpackageProgram.id}

SELECT 
	
FROM sub_pro_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
	AND cb.isBid=1
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE sub_pro_contract SET update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks},
user_id = #{user.id}, employer = #{employer}, connector = #{connector}, phone_num = #{phoneNum}, subpro_id = #{subpackageProgram.id},
start_date = #{startDate}, build_date = #{buildDate}, subpro_addr = #{subProAddr}, subpro_contract_date = #{subProContractDate}, complete_date = #{completeDate},
subpro_total_price = #{subProTotalPrice}, subpro_contract_num = #{subProContractNum}, subpro_contract_name = #{subProContractName}
WHERE id = #{id}

UPDATE sub_pro_contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
