mapper.select:
SELECT 
	
FROM oa_act_contract a

WHERE a.id = #{id}

SELECT 
	
FROM oa_act_contract a

WHERE a.proc_ins_id = #{procInsId}

mapper.update:
UPDATE oa_act_contract SET 
	exe_date = #{exeDate},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}, 
	remarks = #{remarks}
WHERE id = #{id}

UPDATE oa_act_contract SET 
	proc_ins_id = #{procInsId},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_act_contract SET 
	lead_text = #{leadText},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_act_contract SET 
	main_lead_text = #{mainLeadText},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_act_contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
