mapper.select:
SELECT 
	
FROM oa_attach_contract a

WHERE a.id = #{id}

SELECT 
	
FROM oa_attach_contract a

WHERE a.proc_ins_id = #{procInsId}

mapper.update:
UPDATE oa_attach_contract SET 
	proc_ins_id = #{procInsId},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_attach_contract SET 
	sub_lead_text = #{subLeadText},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_attach_contract SET 
	lead_text = #{leadText},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_attach_contract SET 
	main_lead_text = #{mainLeadText},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_attach_contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
